import numpy as np
def normalize_coins(coins):
    arr = np.array(coins, dtype=float)
    min_val = np.min(arr)
    max_val = np.max(arr)
    if max_val != min_val:
        return ((arr - min_val) / (max_val - min_val)).tolist()
    else:
        return arr.tolist()

