import math

def calculate_game_damage(base_damage, critical_hit):
    if critical_hit:
        return math.floor(base_damage * 2.5)
    return base_damage

from collections import Counter

def count_inventory_items(inventory):
    item_counts = Counter(inventory)
    return dict(sorted(item_counts.items(), key=lambda x: (-x[1], x[0])))

from datetime import datetime, timedelta

def calculate_game_time(seconds):
    time_obj = datetime(1, 1, 1) + timedelta(seconds=seconds)
    return time_obj.strftime('%H:%M:%S')

import zlib

def compress_game_data(data):
    data_bytes = data.encode('utf-8')
    compressed_data = zlib.compress(data_bytes)
    return len(compressed_data)

import math

def calculate_experience_points(monster_levels):
    total_xp = 0
    for level in monster_levels:
        total_xp += int(math.pow(level, 2) * 10)
    return total_xp
